<span class="line"><span style="color: #D73A49">export</span><span style="color: #24292E"> </span><span style="color: #D73A49">const</span><span style="color: #24292E"> </span><span style="color: #6F42C1">load</span><span style="color: #D73A49">:</span><span style="color: #24292E"> </span><span style="color: #6F42C1">PageServerLoad</span><span style="color: #24292E"> </span><span style="color: #D73A49">=</span><span style="color: #24292E"> </span><span style="color: #D73A49">async</span><span style="color: #24292E"> (&lbrace; </span><span style="color: #E36209">locals</span><span style="color: #24292E">, </span><span style="color: #E36209">params</span><span style="color: #24292E"> &rbrace;) </span><span style="color: #D73A49">=&gt;</span><span style="color: #24292E"> &lbrace;</span></span>
